import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import style from "./home.module.css"

import { getGames } from "../../redux/actions";
import Cards from "../../components/cards/cards.component";


function Home({ allGenres }) {

  const genresNames = allGenres.map((genre) => genre.name);

  const initialFilters = genresNames.map((genero, index) => ({
    [genero]: false
  }));

  const [filters, setFilters] = useState(initialFilters);
  let allGames = useSelector((state) => state.allGames);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getGames())
    //Aca va lo que pasa cuando se desmonta
    /* return (()=>clearState()) */
  }, [dispatch])

  /* const handleChange = (event) =>{
    useEffect(()=>{

      
    },[dispatch])
    
  } */

  /*  if(event.target.value === true){
     allGames = allGames.filter()
   }
  */

  return (
    <div className={style.container}>

      
      {console.log(filters)}

      <div className={style.filtros}>
        <h1>filtros</h1>

        <h3>Orden</h3>
        <select>
          <option value="-" selected>-</option>
          <option value="Ascendente">Ascendente</option>
          <option value="Descendente">Descendente</option>
        </select>

        <h3>Origen</h3>
        <select>
          <option value="-" selected>-</option>
          <option value="DB">DB Games</option>
          <option value="API">API Games</option>
        </select>

        <h3>Generos</h3>

        {
          allGenres.map((genero) => (
            <label>
              {genero.name}
              <input type="checkbox" key={genero.id} value={genero.name} />
            </label>
          ))
        }
      </div>

      <div className={style.cards}>
        <Cards allGames={allGames} />
      </div>

    </div>
  );
}
export default Home;
