import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getById } from "../../redux/actions";
import { useParams } from "react-router-dom";
import style from "./detail.module.css"

function Detail() {

  const { id } = useParams()

  let game = useSelector((state) => state.gameId);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getById(id))

    //Aca va lo que pasa cuando se desmonta
    /* return (()=>clearState()) */
  }, [dispatch])

  const description = game.description?.replace(/<[^>]*>/g, '');
  return (
    <div className={style.container} >

      <div className={style.lateral}>
        <h2>Filters</h2>

        <h3>Name</h3>
        <select name="Nombre" onChange={handleAscDsc} value={selectedNombre}>
          <option value="-" disabled>-</option>
          <option value="A Nombre">A - Z</option>
          <option value="D Nombre">Z - A</option>
        </select>

        <h3>Rating</h3>
        <select name="Rating" onChange={handleAscDsc} value={selectedRating}>
          <option value="-" disabled>-</option>
          <option value="A Rating" >Ascendent</option>
          <option value="D Rating">Descendent</option>
        </select>

        <h3>Origin</h3>
        <select name="Origen" onChange={handleOrigin}>
          <option value="All" >All</option>
          <option value="DB">DB Games</option>
          <option value="API">API Games</option>
        </select>

        <h3>Generos</h3>
        <div className={style.checkboxs}>
          {allGenres && allGenres.map((genero) => (
            <label key={genero.id}>
              {genero.name}
              <input
                type="checkbox"

                name={genero.name}
                onChange={handleChange}
              />
            </label>
          ))}
        </div>
      </div>



      <div>
        <h1>Pagina de detalles</h1>
        <h3>{game.name}</h3>
        <p>{description}</p>
        <img src={game.background_image} alt="" />
        <h4>Realesed: {game.released}</h4>
        <h4>Rating: {game.rating}</h4>
        <h4>Genres:</h4>
        <ul>
          {game.genres?.map((genre) => (
            <li>{genre.name}</li>
          ))}
        </ul>
        <h4>Avible on:</h4>
        {/* <ul>
        {game.platforms?.map((plat) => (
          <li>{plat.name}</li>
        ))}
      </ul> */}
      </div>

    </div>
  );
}

export default Detail;
