import { CLEAR_STATE, GET_BY_ID, GET_BY_NAME, GET_GAMES, SUMBIT_GAME, GET_GENRES, GET_PLATFORMS, ORDER_NAMRAT, ORDER_UPDOWN } from "../actions";

let initialState = { allGames: [], allGenres: [], platflorms: [], ratname: "" }

function rootReducer(state = initialState, action) {
    switch (action.type) {
        case GET_BY_NAME:
            return {
                ...state,
                allGames: action.payload
            }
        case GET_GAMES:
            return {
                ...state,
                allGames: action.payload
            }
        case GET_BY_ID:
            return {
                ...state,
                allGames: action.payload
            }
        case SUMBIT_GAME:
            return {
                ...state,
                allGames: [...state.allGames, ...action.payload]
            }
        case GET_GENRES:
            return {
                ...state,
                allGenres: action.payload
            }
        case GET_PLATFORMS:
            return {
                ...state,
                platflorms: action.payload
            }
        case ORDER_UPDOWN:
            if (payload === "Ascendente") {
                ordenados = state.allGames.sort((a, b) => (a[ratname] > b[ratname] ? 1 : -1))
            } else {
                ordenados = state.allGames.sort((a, b) => (a[ratname] < b[ratname] ? 1 : -1))
            }
            return {
                ...state,
                platflorms: [...ordenados]
            }

        case ORDER_NAMRAT:
            return {
                ...state,
                ratname: action.payload
            }
        case CLEAR_STATE:
            return {
                allGames: []
            }
        default:
            return state

    }

}

export default rootReducer;
