import { useState } from "react";
import style from "./form.module.css"
import { sumbitGame } from "../../redux/actions";

import { useDispatch } from "react-redux";

import { getGenres } from "../../redux/actions";

function Form() {

  const allGenres = getGenres();

  const [data, setData] = useState({
    name: "",
    description: "",
    realesed: "",
    image: "",
    rating: "",/*
    platafomrs: "",
    genres: "" */
  })

  const [error, setErrors] = useState({
    name: "",
    platafomrs: "",
    image: ""
  })

  const handleChange = (event) => {
    setData({
      ...data,
      [event.target.name]: event.target.value
    })
  }

  const dispatch = useDispatch();

  function handleSumbit(event) {
    event.preventDefault()
    dispatch(sumbitGame(data))
  }

  return (
    <div className={style.container} >
      <h1>Subi tu juego</h1>
      <form onSubmit={handleSumbit}>
        <label >Nombre</label>
        <input
          type="text"
          value={data.name}
          onChange={handleChange}
          placeholder="Name"
          name="name"
        />
        <label >Imagen</label>
        <input
          type="file"
          accept="image/*"
          value=""
          onChange={handleChange}
          placeholder="background_image"
        />
        <label >Descripcion</label>
        <input
          type="text"
          value={data.description}
          onChange={handleChange}
          placeholder="Description"
          name="description"
        />
        <label >Plataformas</label>
        <input
          type="text"
          value=""
          onChange={handleChange}
          placeholder="Plataforms"
          name="plataforms"
        />
        <label >Rating</label>
        <input
          type="number"
          value={data.rating}
          onChange={handleChange}
          placeholder="1 to 10"
          name="rating"
        />
        <label >Fecha de lanzamiento</label>
        <input
          type="date"
          value={data.realesed}
          onChange={handleChange}
          placeholder="Realesed date"
          name="realesed"
        />

{/*         <label >Generos</label>
        <input
          type="text"
          value=""
          onChange={handleChange}
          placeholder="Genres"
          name="genres"
        /> */}
        
        <select>
          {
            allGenres.map((genero)=>{
              <option value={genero.name}>{genero.name}</option>
            })
          }
        </select>

        <button type="sumbit" onClick={handleSumbit}>SUMBITEALO</button>
      </form>
    </div>
  );
}

export default Form;
