import { useState } from "react";
import style from "./form.module.css"
import { sumbitGame } from "../../redux/actions";

import { useDispatch } from "react-redux";

function Form({ allGenres }) {

  const allPlatforms = ["PC", "xBox", "PS4", "Switch", "PS5"];

  const [data, setData] = useState({
    name: "",
    description: "",
    realesed: "",
    background_image: "",
    rating: "",
    platforms: ""/*,
     genres: "" */
  })

  const [error, setErrors] = useState({
    name: "",
    platafomrs: "",
    image: ""
  })

  const handleChange = (event) => {
    setData({
      ...data,
      [event.target.name]: event.target.value
    })
    setErrors(
      validation({
        ...data,
        [event.target.name]: event.target.value
      })
    )
  }

  const dispatch = useDispatch();

  function handleSumbit(event) {
    event.preventDefault()
    dispatch(sumbitGame(data))
  }

  function validation(data){
    let errors={}

    if(!data.name) error.name = "El nombre es obligatorio"

    if(!data.description) error.description = "Agregue una breve descripcion"
    
    if(!data.background_image) error.background_image = "La imagen es obligatoria"

    if(!data.realesed) error.realesed = "Ingrese la fecha de lanzamiento"

    return errors;
  }

  return (
    <div className={style.container} >
      <h1>Subi tu juego</h1>
      <form onSubmit={handleSumbit}>
        <label >Nombre</label>
        <input
          type="text"
          value={data.name}
          onChange={handleChange}
          placeholder="Name"
          name="name"
        />
        <label >Imagen</label>
        <input
          type="text"
          value={data.background_image}
          onChange={handleChange}
          placeholder="background_image"
          name="background_image"
        />
        <label >Descripcion</label>
        <input
          type="text"
          value={data.description}
          onChange={handleChange}
          placeholder="Description"
          name="description"
        />
        <label >Plataformas</label>
        <select>
          <option value={data.platforms} disabled selected>-</option>
          {
            allPlatforms.map((plat) => (
              <option value={plat}>
                {plat}
              </option>
            ))
          }
        </select>

        <label >Rating</label>
        <input
          type="number"
          value={data.rating}
          onChange={handleChange}
          placeholder="1 to 10"
          name="rating"
        />
        <label >Fecha de lanzamiento</label>
        <input
          type="date"
          value={data.realesed}
          onChange={handleChange}
          placeholder="Realesed date"
          name="realesed"
        />
        <label >Generos</label>
        <select>
          <option value={data.genres} disabled selected>-</option>
          {
            allGenres.map((genero) => (
              <option key={genero.id} value={genero.name}>
                {genero.name}
              </option>
            ))
          }
        </select>
        <button type="sumbit" onClick={handleSumbit}>SUMBITEALO</button>
      </form>
    </div>
  );
}

export default Form;
