import { CLEAR_STATE, GET_BY_ID, GET_BY_NAME, GET_GAMES, SUMBIT_GAME, GET_GENRES, GET_PLATFORMS, ORDER_NAMRAT, ORDER_UPDOWN } from "../actions";

let initialState = { allGames: [], allGenres: [], platflorms: [], name: true }

function rootReducer(state = initialState, action) {
    let ordenados;
    switch (action.type) {
        case GET_BY_NAME:
            return {
                ...state,
                allGames: action.payload
            }
        case GET_GAMES:
            return {
                ...state,
                allGames: action.payload
            }
        case GET_BY_ID:
            return {
                ...state,
                allGames: action.payload
            }
        case SUMBIT_GAME:
            return {
                ...state,
                allGames: [...state.allGames, ...action.payload]
            }
        case GET_GENRES:
            return {
                ...state,
                allGenres: action.payload
            }
        case GET_PLATFORMS:
            return {
                ...state,
                platflorms: action.payload
            }
        case ORDER_UPDOWN:
            if (state.name === true) {
                if (action.payload === "Ascendente") {
                    ordenados = state.allGames.sort((a, b) => (a.rating > b.rating ? 1 : -1))
                    console.log(state.ratname)
                } else {
                    ordenados = state.allGames.sort((a, b) => (a.rating < b.rating ? 1 : -1))
                }
                console.log(ordenados)
                return {
                    ...state,
                    allGames: [...ordenados]
                }

            } else

                if (action.payload === "Ascendente") {
                    ordenados = state.allGames.sort((a, b) => (a.rating > b.rating ? 1 : -1))
                    console.log(state.ratname)
                } else {
                    ordenados = state.allGames.sort((a, b) => (a.rating < b.rating ? 1 : -1))
                }


        case ORDER_NAMRAT:
            return {
                ...state,
                name: action.payload
            }
        case CLEAR_STATE:
            return {
                allGames: []
            }
        default:
            return state

    }

}

export default rootReducer;
