import { CLEAR_STATE, GET_BY_NAME, GET_GAMES } from "../actions";

let initialState = {allGames:[]}

function rootReducer(state=initialState, action){
    switch(action.type){
        case GET_BY_NAME:
            return{
                ...state,
                allGames: action.payload
            }
        case GET_GAMES:
            return {
                ...state,
                allGames: action.payload
            }
        case CLEAR_STATE:
            return{
                allGames: action.payload
            }
        default:
            return state

    }

}

export default rootReducer;
