import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import style from "./home.module.css"

import { orderUpDown, getGames, orderNamRat, filterOrigin, filterGenres } from "../../redux/actions";
import Cards from "../../components/cards/cards.component";


function Home({ allGenres }) {

  const [filters, setFilters] = useState({});

  const [orden, setOrden] = useState({});

  let allGames = useSelector((state) => state.allGames);

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getGames())
    //Aca va lo que pasa cuando se desmonta
    /* return (()=>clearState()) */
  }, [dispatch])

  console.log("hola")

  const handleChange = (event) => {
    const { name, checked } = event.target;
    setFilters({
      ...filters,
      [name]: checked
    });
  };


  const handleOrderUpDown = (event) => {
    setOrden({
      ...orden,
      upDown: event.target.value
    })
    dispatch(orderUpDown(event.target.value))
  }

  const handleOrderNamRat = (event) => {
    console.log('Order Name/Rating action triggered with value:', event.target.value);
    dispatch(orderNamRat(event.target.value))
  }

  const handleOrigin = (event) => {

    dispatch(filterOrigin(event.target.value))
  }

  return (
    <div className={style.container}>


      {console.log(filters)}

      <div className={style.filtros}>
        <h1>filtros</h1>

        <h3>Orden</h3>
        <select onChange={handleOrderUpDown}>
          <option value="-" selected >-</option>
          <option value="Ascendente">Ascendente</option>
          <option value="Descendente">Descendente</option>
        </select>

        <h3>Rating</h3>
        <select onChange={handleOrderNamRat}>
          <option value="name" selected>Nombre </option>
          <option value="rating">Rating </option>
        </select>

        <h3>Origen</h3>
        <select onChange={handleOrigin}>
          <option value="-" selected>-</option>
          <option value="DB">DB Games</option>
          <option value="API">API Games</option>
        </select>

        <h3>Generos</h3>

        {allGenres.map((genero) => (
          <label key={genero.id}>
            {genero.name}
            <input
              type="checkbox"
              name={genero.name}
              checked={filters[genero.name] || false}
              onChange={handleChange}
            />
          </label>
        ))}

      </div>

      <div className={style.cards}>
        <Cards allGames={allGames} />
      </div>

    </div>
  );
}
export default Home;
